import React from 'react';
import { useLocation } from 'react-router-dom';
import '../Common Page/common.css'; // Import the CSS file

const Common = () => {
  const location = useLocation();

  let heading = '';
  let description = '';

  if (location.pathname === '/private-limited-company') {
    heading = 'Be an Entrepreneur';
    description = 'Start your journey as a business owner today!';
  } else if (location.pathname === '/limited-liabilty-partnership') {
    heading = 'Registration';
    description = 'Register now to become a member!';
  }

  return (
    <div className="common-container">
      <div className="common-card">
        <h1 className="common-heading">{heading}</h1>
        <p className="common-description">{description}</p>
        <form className="common-form">
          <input type="text" placeholder="Your name" className="common-input" />
          <input type="email" placeholder="Email address" className="common-input" />
          <button type="submit" className="common-button">Submit</button>
        </form>
      </div>
    </div>
  );
};

export default Common;
