import React from 'react';
import '../AboutUs/aboutus.css';

const teamMembers = [
  {
    name: 'John Doe',
    role: 'Founder & CEO',
    description: 'John brings over 15 years of experience in business compliance and corporate strategy. His vision is to simplify entrepreneurship for everyone.',
    image: '/images/john.jpg'
  },
  {
    name: 'Jane Smith',
    role: 'COO',
    description: 'Jane oversees the company’s operations, ensuring excellence in client delivery and team performance.',
    image: '/images/jane.jpg'
  },
  {
    name: 'Michael Ray',
    role: 'CTO',
    description: 'Michael drives our technology innovations, ensuring we’re always ahead in delivering seamless digital experiences.',
    image: '/images/michael.jpg'
  }
];

const AboutUs = () => {
  return (
    <div className="aboutus-container">
      <div className="aboutus-note-card">
        <h2>About Us</h2>
        <p>
          We are a team of passionate professionals dedicated to simplifying the process of starting and managing a business in India. 
          Our platform is designed to assist entrepreneurs with registrations, legal compliance, and expert consultation—all in one place.
        </p>
      </div>

      <div className="team-section">
        <h2>Our Team</h2>
        {teamMembers.map((member, index) => (
          <div className={`team-member ${index % 2 !== 0 ? 'reverse' : ''}`} key={index}>
            <img src={member.image} alt={member.name} className="team-image" />
            <div className="team-details">
              <h3>{member.name}</h3>
              <p className="role">{member.role}</p>
              <p>{member.description}</p>
            </div>
          </div>
        ))}
      </div>
    </div>
  );
};

export default AboutUs;
