import React, { useState } from 'react';
import '../Navbar/navbar.css';

const Navbar = () => {
  const [activeMenu, setActiveMenu] = useState(null);
  const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

  const menuItems = [
    {
      title: 'Be An Entrepreneur',
      submenu: [
        { title: 'Web Development', link: '/services/web' },
        { title: 'App Development', link: '/services/app' },
        { title: 'UI/UX Design', link: '/services/design' },
      ],
    },
    {
      title: 'Services',
      submenu: [
        { title: 'Web Development', link: '/services/web' },
        { title: 'App Development', link: '/services/app' },
        { title: 'UI/UX Design', link: '/services/design' },
      ],
    },
    {
      title: 'About',
      link: '/about',
    },
    {
      title: 'Contact',
      submenu: [
        { title: 'Email', link: '/contact/email' },
        { title: 'Phone', link: '/contact/phone' },
      ],
    },
  ];

  const toggleMobileMenu = () => {
    setMobileMenuOpen(!mobileMenuOpen);
  };

  const toggleSubmenu = (index) => {
    setActiveMenu(activeMenu === index ? null : index);
  };

  return (
    <nav className="navbar">
      <div className="navbar-container">
        <div className="brand">NS LegalHelp</div>
        <button className="menu-toggle" onClick={toggleMobileMenu}>
          â˜°
        </button>
        <ul className={`menu ${mobileMenuOpen ? 'open' : ''}`}>
          {menuItems.map((item, index) => (
            <li
              key={index}
              className={`menu-item ${item.submenu ? 'has-submenu' : ''}`}
              onMouseEnter={() => !mobileMenuOpen && setActiveMenu(index)}
              onMouseLeave={() => !mobileMenuOpen && setActiveMenu(null)}
              onClick={() => mobileMenuOpen && toggleSubmenu(index)}
            >
              <a href={item.link || '#'}>{item.title}</a>
              {item.submenu && activeMenu === index && (
                <ul className="submenu">
                  {item.submenu.map((subItem, subIndex) => (
                    <li key={subIndex} className="submenu-item">
                      <a href={subItem.link}>{subItem.title}</a>
                    </li>
                  ))}
                </ul>
              )}
            </li>
          ))}
        </ul>
      </div>
    </nav>
  );
};

export default Navbar;
