import React from 'react';
import '../AboutUs/aboutus.css';
// import team1 from '../Assets/team1.jpg'; 
// import team2 from '../Assets/team2.jpg';

const AboutUs = () => {
  const teamMembers = [
    {
      name: 'Rahul Sharma',
      image: "team1",
      role: 'Founder & CEO',
      description: 'Rahul brings 10+ years of startup and compliance experience. He leads our vision and client relationships with precision and trust.',
    },
    {
      name: 'Priya Desai',
      image: "team2",
      role: 'Head of Operations',
      description: 'Priya ensures smooth execution of services with her sharp attention to detail and focus on customer satisfaction.',
    },
  ];

  return (
    <div className="about-container">
      <div className="about-note">
        <h1>About Us</h1>
        <p>
          We are a passionate team dedicated to helping individuals and businesses start and manage their enterprises with ease. Our platform bridges the gap between compliance and entrepreneurs.
        </p>
      </div>

      <h2 className="team-heading">Our Team</h2>
      {teamMembers.map((member, index) => (
        <div className="team-section" key={index}>
          <div className="team-image">
            <img src={member.image} alt={member.name} />
          </div>
          <div className="team-info">
            <h3>{member.name}</h3>
            <p className="team-role">{member.role}</p>
            <p>{member.description}</p>
          </div>
        </div>
      ))}
    </div>
  );
};

export default AboutUs;
