const menuExtraInfo = {
    "Private Limited Company": {
      Essential: [
<p className='doc-requird' style={{ textAlign: 'left',fontSize:'15px' }}>
                                      1. Photographs of all directors.<br />
                                      2. Self-attested PAN Card of all directors.<br />
                                      3. Self-attested Adhar Card of all directors.<br />
                                      4. Self-attested ID Proof of all directors (Driving License/Passport/Voter ID).<br />
                                      5. Self-Attested Address Proof of all directors (Electricity Bill/Latest Bank Statement/Mobile Bill) (not older than 2 months).<br />
                                      6. Notarized Rent agreement.<br />
                                      7. Notarized NOC (We will share the draft).<br />
                                      8. Notarized Electricity Bill of the Registered Office (not older than 2 months).<br /><br />
                                       <p style={{color:'black' , fontSize:'10px'}}>
                                           Looking forward to collaborate with you soon and serving you with our best professional services.
                                      </p>
                                  </p>
      ],
      Enhanced: [
        "Everything in Essential",
        "GST Registration",
        "MSME Certificate",
        "One-year Compliance Support"
      ]
    },
    "LLP Registration": {
      Essential: [
        "LLP Name Reservation",
        "Incorporation Certificate",
        "PAN & TAN"
      ],
      Enhanced: [
        "Everything in Essential",
        "GST Registration",
        "MSME Certificate",
        "One-year Accounting Support"
      ]
    },
    // Add more menus as needed
  };
  export default menuExtraInfo;

