import React, { useState } from 'react';
import '../Navbar/navbar.css';

const Navbar = () => {
    const [activeMenu, setActiveMenu] = useState(null);
    const [mobileMenuOpen, setMobileMenuOpen] = useState(false);

    const menuItems = [
        {
            title: 'Home',
            submenu: {
                heading: 'Explore',
                columns: [
                    [
                        { heading: 'Services', items: [
                            { title: 'Legal Documentation', link: '/home/legal-docs' },
                            { title: 'Startup Registration', link: '/home/startup-registration' },
                        ]}
                    ],
                    [
                        { heading: 'Support', items: [
                            { title: 'Live Chat', link: '/home/live-chat' },
                            { title: 'FAQs', link: '/home/faqs' },
                        ]}
                    ]
                ]
            }
        },
        {
            title: 'Be An Entrepreneur',
            submenu: {
                heading: 'For Indian Entrepreneur',
                columns: [
                    [
                        { heading: 'Development', items: [
                            { title: 'Web Development', link: '/services/web' },
                            { title: 'App Development', link: '/services/app' },
                        ]}
                    ],
                    [
                        { heading: 'Creative', items: [
                            { title: 'UI/UX Design', link: '/services/design' },
                            { title: 'SEO', link: '/services/seo' },
                        ]}
                    ]
                ]
            }
        },
        {
            title: 'About',
            link: '/about',
        },
        {
            title: 'Contact',
            submenu: [
                { title: 'Email', link: '/contact/email' },
                { title: 'Phone', link: '/contact/phone' },
            ],
        },
    ];

    const toggleMobileMenu = () => {
        setMobileMenuOpen(!mobileMenuOpen);
    };

    const toggleSubmenu = (index) => {
        setActiveMenu(activeMenu === index ? null : index);
    };

    return (
        <nav className="navbar">
            <div className="navbar-container">
                <div className="brand">
                    <img src="/logo.png" alt="NS LegalHelp Logo" className="logo" />
                </div>

                <button className="menu-toggle" onClick={toggleMobileMenu}>
                    â˜°
                </button>
                <ul className={`menu ${mobileMenuOpen ? 'open' : ''}`}>
                    {menuItems.map((item, index) => (
                        <li
                            key={index}
                            className={`menu-item ${item.submenu ? 'has-submenu' : ''}`}
                            onMouseEnter={() => !mobileMenuOpen && setActiveMenu(index)}
                            onMouseLeave={() => !mobileMenuOpen && setActiveMenu(null)}
                            onClick={() => mobileMenuOpen && toggleSubmenu(index)}
                        >
                            <a href={item.link || '#'}>{item.title}</a>

                            {/* Mega Submenu */}
                            {item.submenu && typeof item.submenu === 'object' && activeMenu === index && (
                                <div className="mega-submenu">
                                    <div className="submenu-heading">{item.submenu.heading}</div>
                                    <div className="submenu-columns">
                                        {item.submenu.columns.map((column, colIndex) => (
                                            <div key={colIndex} className="submenu-column">
                                                {column.map((colItem, subIndex) => (
                                                    <div key={subIndex}>
                                                        <div className="submenu-column-heading">{colItem.heading}</div>
                                                        <ul>
                                                            {colItem.items.map((subItem, i) => (
                                                                <li key={i} className="submenu-item">
                                                                    <a href={subItem.link}>{subItem.title}</a>
                                                                </li>
                                                            ))}
                                                        </ul>
                                                    </div>
                                                ))}
                                            </div>
                                        ))}
                                    </div>
                                </div>
                            )}

                            {/* Simple Submenu */}
                            {Array.isArray(item.submenu) && activeMenu === index && (
                                <ul className="submenu">
                                    {item.submenu.map((subItem, subIndex) => (
                                        <li key={subIndex} className="submenu-item">
                                            <a href={subItem.link}>{subItem.title}</a>
                                        </li>
                                    ))}
                                </ul>
                            )}
                        </li>
                    ))}
                </ul>
            </div>
        </nav>
    );
};

export default Navbar;
